import React from "react"
import Rellax from "rellax";
import {Link} from "react-router-dom";
import {Translation} from "react-i18next";
import i18n from "../../i18n";
import {QueryRenderer} from "react-relay";
import environment from "../../environment";

const $ = window.jQuery;

function setBanner() {
    let feedback = $('.feedback');
    feedback.slick({
        infinite: true,
        slidesToScroll: 1,
        appendArrows: $(".slide-arrow"),
        prevArrow: "<img src='/assets/images/home/arrow-slide-left.svg' alt='arrow slide left' class='slick-prev'/>",
        nextArrow: "<img src='/assets/images/home/arrow-slide-right.svg' alt='arrow slide right' class='slick-next'/>",
        variableWidth: true
    });
}


class HeaderHome extends React.Component {

    componentDidMount() {
        new Rellax('.rellax', {
            wrapper: null,
            vertical: true,
            horizontal: true
        });
    }


    render() {
        setTimeout(() => {
            setBanner()
        }, 100);
        return (
            <Translation>
                {(t) => (
                    <React.Fragment>

                        <div className="header">
                            <div className="container">
                                <div className="row">
                                    <div className="col-12">
                                        <div className="d-none d-sm-none d-md-block">
                                            <div className="banner animated fadeInUp">

                                                <div id="carouselExampleControls" className="carousel slide"
                                                     data-ride="carousel">
                                                    <div className="carousel-inner">
                                                        <div className="carousel-item active">
                                                            <img className="d-block w-100" src="https://picsum.photos/seed/picsum/200/300" alt="First slide"/>
                                                        </div>
                                                        <div className="carousel-item">
                                                            <img className="d-block w-100" src="https://picsum.photos/seed/picsum/200/300" alt="Second slide"/>
                                                        </div>
                                                        <div className="carousel-item">
                                                            <img className="d-block w-100" src="https://picsum.photos/seed/picsum/200/300" alt="Third slide"/>
                                                        </div>
                                                    </div>
                                                    <a className="carousel-control-prev" href="#carouselExampleControls"
                                                       role="button" data-slide="prev">
                                                        <span className="carousel-control-prev-icon"
                                                              aria-hidden="true"></span>
                                                        <span className="sr-only">Previous</span>
                                                    </a>
                                                    <a className="carousel-control-next" href="#carouselExampleControls"
                                                       role="button" data-slide="next">
                                                        <span className="carousel-control-next-icon" aria-hidden="true"></span>
                                                        <span className="sr-only">Next</span>
                                                    </a>
                                                </div>
                                                {/*<div className="description rellax" data-rellax-speed="8">*/}
                                                {/*    /!*<h2>{t('home:State-Of-The-Art Technology')}</h2>*!/*/}
                                                {/*    <h2 dangerouslySetInnerHTML={{__html: t("home:State-Of-The-Art Technology")}}/>*/}
                                                {/*    /!*<h1 dangerouslySetInnerHTML={{__html: t("home:For Property Management")}}/>*!/*/}
                                                {/*    <p>{t('home:Innovation that makes life easier for residents and property managers. ' +*/}
                                                {/*        'Simplify all aspects of work from ACCOUNTING to MANAGEMENT')}</p>*/}
                                                {/*</div>*/}
                                                {/*<div className="pc-mobile">*/}
                                                {/*    <img*/}
                                                {/*        src={process.env.PUBLIC_URL + '/assets/images/home/notebook-header.png'}*/}
                                                {/*        alt="silverman application" className="pc rellax"*/}
                                                {/*        data-rellax-speed="4"/>*/}
                                                {/*    <img*/}
                                                {/*        src={process.env.PUBLIC_URL + '/assets/images/home/mobile-header.png'}*/}
                                                {/*        data-rellax-speed="14"*/}
                                                {/*        alt="silverman mobile application" className="mobile rellax"/>*/}
                                                {/*</div>*/}
                                            </div>
                                        </div>

                                        <div className="mobile-banner d-block d-sm-block d-md-none">
                                            <div className="description">
                                                <div className="rellax" data-rellax-speed="-1">
                                                    <h2 dangerouslySetInnerHTML={{__html: t("home:State-Of-The-Art Technology")}}/>
                                                    {/*<h2>{t('home:State-Of-The-Art Technology')}</h2>*/}
                                                    {/*<h1 dangerouslySetInnerHTML={{__html: t("home:For Property Management")}}/>*/}
                                                </div>
                                                <div className="pc-mobile-responsive">
                                                    <img className="rellax" data-rellax-speed="4"
                                                         src={process.env.PUBLIC_URL + '/assets/images/home/home-banner-responsive.png'}
                                                         alt="silverman application"/>
                                                </div>
                                                <div className="rellax" data-rellax-speed="1">
                                                    <p>{t('home:Innovation that makes life easier for residents and property managers. ' +
                                                        'Simplify all aspects of work from ACCOUNTING to MANAGEMENT')}</p>
                                                    <Link
                                                        to={(i18n.language !== "th" ? "/" + i18n.language : "") + "/register"}>
                                                        <button className="btn btn-primary">
                                                            {t('home:Register')}
                                                        </button>
                                                    </Link>
                                                    <p>{t('home:Getting to know us.')}</p>
                                                    <img
                                                        src={process.env.PUBLIC_URL + '/assets/images/home/arrow-down.svg'}
                                                        alt="scroll down"
                                                        className="scroll-down animated bounce infinite"/>
                                                </div>
                                            </div>
                                        </div>

                                        <div className="d-none d-sm-none d-md-block">
                                            <div className="scroll-down-box scrollOut fadeInUp">
                                                <p>{t('home:Getting to know us.')}</p>
                                                <img src={process.env.PUBLIC_URL + '/assets/images/home/mouse.svg'}
                                                     alt="scroll down" className="scroll-down"/>
                                                <img src={process.env.PUBLIC_URL + '/assets/images/home/arrow-down.svg'}
                                                     alt="scroll down"
                                                     className="scroll-down animated bounce infinite"/>
                                            </div>
                                        </div>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </React.Fragment>

                )}
            </Translation>
        )
    }
}

export default HeaderHome;
