import React from 'react';
import './style.scss'
import {Link} from "react-router-dom";
import _ from "lodash";
import MainNavigationAbout from "./about/mainNavigationAbout";
import MobileNavigation from "./mobileNavigation";
import {Translation} from "react-i18next";
import i18n from "../../i18n";

const $ = window.jQuery;

class Navigation extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            // backgroundOpacity: 0,
            isMount: false
        };
        this.getUrl = this.getUrl.bind(this);
    }

    componentDidMount() {
        // this.isMount = true;
        // let _this = this;
        // let navigation = $('#navigation');
        // let has_white = navigation.hasClass("white");
        // $(window).scroll(function () {
        //     if (_this.isMount) {
        //         // _this.setState({backgroundOpacity: $(document).scrollTop() / 400 > 1 ? 1 : $(document).scrollTop() / 400})
        //
        //         if ($(document).scrollTop() > 20 && !has_white) {
        //             navigation.addClass('white');
        //         } else if ($(document).scrollTop() < 20 && !has_white) {
        //             navigation.removeClass('white');
        //         }
        //     }
        // });

        $('.dropdown-menu a.dropdown-toggle').on('click', function (e) {
            if (!$(this).next().hasClass('show')) {
                $(this).parents('.dropdown-menu').first().find('.show').removeClass("show");
            }
            let $subMenu = $(this).next(".dropdown-menu");
            $subMenu.toggleClass('show');


            $(this).parents('li.nav-item.dropdown.show').on('hidden.bs.dropdown', function (e) {
                $('.dropdown-submenu .show').removeClass("show");
            });

            return false;
        });
    }

    componentWillUnmount() {
        this.isMount = false;
    }

    getUrl(lang) {
        let pathname = window.location.pathname;
        if (lang === 'en') {
            return "/en" + pathname.replace("/en", "");
        } else {
            return pathname.replace("/en", "");
        }
    }

    render() {
        return (
            <Translation>
                {(t) => (

                    <nav className="navbar navbar-expand-lg navbar-dark primary-color">

  <a className="navbar-brand" href="#">Navbar</a>

  <button className="navbar-toggler" type="button" data-toggle="collapse" data-target="#basicExampleNav"
    aria-controls="basicExampleNav" aria-expanded="false" aria-label="Toggle navigation">
    <span className="navbar-toggler-icon"/  >
  </button>

  <div className="collapse navbar-collapse" id="basicExampleNav">
        <ul className="navbar-nav mr-auto">
      <li className="nav-item active">
        <a className="nav-link" href="#">Home
          <span className="sr-only">(current)</span>
        </a>
      </li>
      <li className="nav-item">
        <a className="nav-link" href="#">Features</a>
      </li>
      <li className="nav-item">
        <a className="nav-link" href="#">Pricing</a>
      </li>
            <li className="nav-item dropdown multi-level-dropdown">
        <a href="#" id="menu" data-toggle="dropdown" className="nav-link dropdown-toggle w-100">Dropdown</a>
