import React from 'react';
import './style.scss'
import {Link} from "react-router-dom";
import _ from "lodash";
import MainNavigationAbout from "./about/mainNavigationAbout";
import MobileNavigation from "./mobileNavigation";
import {Translation} from "react-i18next";
import i18n from "../../i18n";

const $ = window.jQuery;

class Navigation extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            backgroundOpacity: 0,
            isMount: false
        };
        this.getUrl = this.getUrl.bind(this);
    }

    componentDidMount() {
        this.isMount = true;
        let _this = this;
        let navigation = $('#navigation');
        let has_white = navigation.hasClass("white");
        $(window).scroll(function () {
            if (_this.isMount) {
                _this.setState({backgroundOpacity: $(document).scrollTop() / 400 > 1 ? 1 : $(document).scrollTop() / 400})

                if ($(document).scrollTop() > 400 && !has_white) {
                    navigation.addClass('white');
                } else if ($(document).scrollTop() < 400 && !has_white) {
                    navigation.removeClass('white');
                }
            }
        });
    }

    componentWillUnmount() {
        this.isMount = false;
    }

    getUrl(lang) {
        let pathname = window.location.pathname;
        if (lang === 'en') {
            return "/en"+pathname.replace("/en", "");
        } else {
            return pathname.replace("/en", "");
        }
    }

    render() {
        return (
            <Translation>
                {(t) => (
                    <div className="navbar navbar-default">
                        <ul className="nav navbar-nav">
                            <li className="dropdown active">
                                <a href="#" className="dropdown-toggle"
                                   data-toggle="dropdown">หน้าแรก
                                    <span className="caret"/>
                                </a>
                                <ul className="dropdown-menu" role="menu">
                                    <li>
                                        <div className="content">สร้าง Content เพิ่มในส่วนนี้</div>
                                    </li>
                                </ul>
                            </li>
                            <li>
                                <a href="#">ผลิตภัณฑ์</a>
                            </li>
                        </ul>
                    </div>
                )}
            </Translation>
        );
    }
}

export default Navigation
