import React from 'react';
import {Link} from "react-router-dom";
import {graphql} from "babel-plugin-relay/macro";
import environment from "../../environment";
import {QueryRenderer} from "react-relay";
import {Translation} from "react-i18next";
import i18n from "../../i18n";

const $ = window.jQuery;

const query = graphql`
    query feedbackHomeQuery {
        customerViewer {
            allCustomer {
                edges {
                    node {
                        id name position quote thumbnail
                    }
                }
            }
        }
    }
`;

function setBanner() {
    $('.feedback').slick({
        appendArrows: $(".slide-arrow"),
        infinite: true,
        speed: 500,
        slidesToShow: 1,
        variableWidth: true,
        prevArrow: "<img src='/assets/images/home/arrow-slide-left.svg' alt='arrow slide left' class='slick-prev'/>",
        nextArrow: "<img src='/assets/images/home/arrow-slide-right.svg' alt='arrow slide right' class='slick-next'/>",
        autoplay: false,
        autoplaySpeed: 4000,
    });

    $('.feedback-mobile').slick({
        arrows: false,
        infinite: true,
        speed: 500,
        slidesToShow: 1,
        variableWidth: true,
        autoplay: true,
        autoplaySpeed: 4000,
        // dots: true
    });
}

const customerList = ({error, props}) => {

    if (error) {
        return <div>{error.message}</div>;
    } else if (props) {
        setTimeout(() => {
            setBanner()
        }, 100);
        return (
            props.customerViewer.allCustomer.edges.map((customer) => (
                <div className="slide" key={customer.node.id}>
                    <div>
                        <img src={customer.node.thumbnail}
                             alt={customer.node.name} className="img-slide"/>
                    </div>
                    <div className="description">
                        <div className="comment">
                            <h2>{customer.node.quote}</h2>
                        </div>
                        <div className="credit">
                            <h3>{customer.node.name}</h3>
                            <p>{customer.node.position}</p>
                        </div>
                        <Link to={(i18n.language !== "th" ? "/"+i18n.language : "") + "/customer"}>
                            {
                                t => <React.Fragment>{t('home:Read More')}</React.Fragment>
                            }
                            <img src={process.env.PUBLIC_URL + '/assets/images/home/arrow-right-blue.svg'}
                                 alt="arrow right blue" className="arrow"/>
                        </Link>
                    </div>
                </div>
            )))
    }
    return <div className="text-center">Loading ...</div>;
};


const customerListMobile = ({error, props}) => {

    if (error) {
        return <div>{error.message}</div>;
    } else if (props) {
        return (
            props.customerViewer.allCustomer.edges.map((customer) => (
                <div className="slide-mobile" key={customer.node.id}>
                    <div className="description">
                        <div className="comment">
                            <p>{customer.node.quote}</p>
                        </div>
                        <div className="credit">
                            <div className="image-cover">
                                <img src={customer.node.thumbnail} alt={customer.node.name}/>
                            </div>
                            <div className="name">
                                <h3>{customer.node.name}</h3>
                                <p>{customer.node.position}</p>
                            </div>
                        </div>
                        <Link to={(i18n.language !== "th" ? "/"+i18n.language : "") + "/customer"}>
                            {
                                t => <React.Fragment>{t('home:Read More')}</React.Fragment>
                            }
                            <img src={process.env.PUBLIC_URL + '/assets/images/home/arrow-right-blue.svg'}
                                 alt="arrow right blue" className="arrow"/>
                        </Link>
                    </div>
                </div>
            )))
    }
    return <div className="text-center">Loading ...</div>;
};


class FeedbackHome extends React.PureComponent {

    render() {
        return (
            <Translation>
                {(t) => (
                    <div className="feedback-home">
                        <div className="container">
                            <div className="row">
                                <div className="col-12">
                                    <h1>{t("home:SEE WHY OVER 100 Projects USE Silverman DAILY")}</h1>
                                    <div className="feedback mt-5 mb-4 d-none d-sm-none d-md-block">
                                        <QueryRenderer
                                            environment={environment}
                                            query={query}
                                            variables={{}}
                                            render={customerList}
                                        />
                                    </div>

                                    <div className="feedback-mobile mt-5 mb-4 d-block d-sm-block d-md-none">
                                        <QueryRenderer
                                            environment={environment}
                                            query={query}
                                            variables={{}}
                                            render={customerListMobile}
                                        />
                                    </div>

                                    <div className="slide-arrow d-none d-sm-none d-md-block"/>
                                </div>
                            </div>
                        </div>
                    </div>
                )}
            </Translation>
        )
    }
}

export default FeedbackHome;
