import React from 'react'
import {Link} from "react-router-dom";
import {Translation} from "react-i18next";
import i18n from "../../i18n";

class FeaturePlatformHome extends React.PureComponent {
    render() {
        return (
            <Translation>
                {(t) => (
                    <div className="container">
                        <div className="feature-platform">
                            <div className="row mb-5">
                                <div className="col-12 text-center">
                                    <h1>{t("home:Make Things Simpler For Better Living")}</h1>
                                    <h2>SILVERMAN PLATFORM</h2>
                                </div>
                            </div>
                            <div className="row mt-5">
                                <div className="col-md-7 text-center">
                                    <img src={process.env.PUBLIC_URL + '/assets/images/home/old-platform.svg'}
                                         alt="ระบบบริหารจัดการแบบเก่า ยุ่งยาก ทำงานซับซ้อน"
                                         className="old-platform img-fluid d-block d-sm-block d-md-none"/>
                                    <h3>{t("home:OTHER PLATFORMS")}</h3>
                                    <p>{t("home:Complicated and redundant")}</p>
                                    <img src={process.env.PUBLIC_URL + '/assets/images/home/old-platform.svg'}
                                         alt="ระบบบริหารจัดการแบบเก่า ยุ่งยาก ทำงานซับซ้อน"
                                         className="old-platform img-fluid delay-300ms slower scrollIn d-none d-sm-none d-md-block"/>
                                </div>
                                <div className="col-md-5 text-center">
                                    <img src={process.env.PUBLIC_URL + '/assets/images/home/new-platform.svg'}
                                         alt="ลดขั้นตอน สะดวกรวดเร็ว"
                                         className="silverman-platform img-fluid d-block d-sm-block d-md-none mt-4"/>
                                    <h3>{t("home:SILVERMAN PLATFORM")}</h3>
                                    <p>{t("home:Simple and Fast")}</p>
                                    <img src={process.env.PUBLIC_URL + '/assets/images/home/new-platform.svg'}
                                         alt="ลดขั้นตอน สะดวกรวดเร็ว"
                                         className="silverman-platform img-fluid slower delay-300ms scrollIn d-none d-sm-none d-md-block"/>
                                </div>
                                <div className="col-12 text-center mb-2">
                                    <Link to={(i18n.language !== "th" ? "/"+i18n.language : "") + "/for-resident"}>
                                        <button className="btn btn-primary delay-300ms scrollIn">
                                            {t("home:Read More")}
                                            <img
                                                src={process.env.PUBLIC_URL + '/assets/images/home/arrow-right.svg'}
                                                alt="arrow-right" className="arrow"/>
                                        </button>
                                    </Link>
                                </div>
                            </div>
                        </div>
                    </div>
                )}
            </Translation>
        )
    }
}

export default FeaturePlatformHome;
