const $ = window.jQuery;

class Navigation extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            backgroundOpacity: 0,
            isMount: false
        };
        this.getUrl = this.getUrl.bind(this);
    }

    componentDidMount() {
        this.isMount = true;
        let _this = this;
        let navigation = $('#navigation');
        let has_white = navigation.hasClass("white");
        $(window).scroll(function () {
            if (_this.isMount) {
                _this.setState({backgroundOpacity: $(document).scrollTop() / 400 > 1 ? 1 : $(document).scrollTop() / 400})

                if ($(document).scrollTop() > 400 && !has_white) {
                    navigation.addClass('white');
                } else if ($(document).scrollTop() < 400 && !has_white) {
                    navigation.removeClass('white');
                }
            }
        });

        $('.dropdown-menu a.dropdown-toggle').on('click', function (e) {
            if (!$(this).next().hasClass('show')) {
                $(this).parents('.dropdown-menu').first().find('.show').removeClass("show");
            }
            let $subMenu = $(this).next(".dropdown-menu");
            $subMenu.toggleClass('show');


            $(this).parents('li.nav-item.dropdown.show').on('hidden.bs.dropdown', function (e) {
                $('.dropdown-submenu .show').removeClass("show");
            });

            return false;
        });
    }

    componentWillUnmount() {
        this.isMount = false;
    }

    getUrl(lang) {
        let pathname = window.location.pathname;
        if (lang === 'en') {
            return "/en" + pathname.replace("/en", "");
        } else {
            return pathname.replace("/en", "");
        }
    }

