import React from 'react';
import './style.scss'
import {Link} from "react-router-dom";
import _ from "lodash";
import MainNavigationAbout from "./about/mainNavigationAbout";
import MobileNavigation from "./mobileNavigation";
import {Translation} from "react-i18next";
import i18n from "../../i18n";

const $ = window.jQuery;

class Navigation extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            backgroundOpacity: 0,
            isMount: false
        };
        this.getUrl = this.getUrl.bind(this);
    }

    componentDidMount() {
        this.isMount = true;
        let _this = this;
        let navigation = $('#navigation');
        let has_white = navigation.hasClass("white");
        $(window).scroll(function () {
            if (_this.isMount) {
                _this.setState({backgroundOpacity: $(document).scrollTop() / 400 > 1 ? 1 : $(document).scrollTop() / 400})

                if ($(document).scrollTop() > 400 && !has_white) {
                    navigation.addClass('white');
                } else if ($(document).scrollTop() < 400 && !has_white) {
                    navigation.removeClass('white');
                }
            }
        });

        $('.dropdown-menu a.dropdown-toggle').on('click', function (e) {
            if (!$(this).next().hasClass('show')) {
                $(this).parents('.dropdown-menu').first().find('.show').removeClass("show");
            }
            let $subMenu = $(this).next(".dropdown-menu");
            $subMenu.toggleClass('show');


            $(this).parents('li.nav-item.dropdown.show').on('hidden.bs.dropdown', function (e) {
                $('.dropdown-submenu .show').removeClass("show");
            });

            return false;
        });
    }

    componentWillUnmount() {
        this.isMount = false;
    }

    getUrl(lang) {
        let pathname = window.location.pathname;
        if (lang === 'en') {
            return "/en" + pathname.replace("/en", "");
        } else {
            return pathname.replace("/en", "");
        }
    }

    render() {
        return (
            <Translation>
                {(t) => (
                    <section className="header-site header-left layers-logo-">
                        <div className="container header-block">
                            <div className="logo">

                                <a href="/" className="custom-logo-link" rel="home" itemProp="url">
                                    <img width="200"
                                         height="75"
                                         src="/assets/images/Logo.png"
                                         className="custom-logo"
                                         alt="Lebaton Music"
                                         itemProp="logo"/></a>
                            </div>

                            <nav className="nav nav-horizontal">

                                <ul id="menu-main-menu-1" className="menu">
                                    <li className="main-li"><a href="/" className="main-a">หน้าแรก</a></li>
                                    <li className="main-li"><a href="/#promotion" className="main-a">โปรโมชัน</a></li>
                                    <li className="main-li"><a href="/#course" className="main-a">หลักสูตรที่เปิดสอน</a>
                                        <ul className="sub-menu">
                                            <li className=""><a href="">Private Classes</a>
                                                <ul className="sub-menu">
                                                    <li className=""><a href="">Voice Lessons</a></li>
                                                    <li className=""><a href="">Piano Lessons</a></li>
                                                    <li className=""><a href="">Keyboard Lessons</a></li>
                                                    <li className=""><a href="">Violin Lessons</a></li>
                                                    <li className=""><a href="">Guitar / Base / Ukulele Lessons</a></li>
                                                    <li className=""><a href="">Drum Lessons</a></li>
                                                    <li className=""><a href="">Flute Lessons</a></li>
                                                    <li className=""><a href="">Saxophone Lessons</a></li>
                                                    <li className=""><a href="">Clarinet Lessons</a></li>
                                                    <li className=""><a href="">Adult Lessons</a></li>
                                                </ul>
                                            </li>
                                            <li className=""><a href="">Group Classes</a>
                                                <ul className="sub-menu">
                                                    <li className=""><a href="">Voice Groups</a></li>
                                                    <li className=""><a href="">Piano Groups</a></li>
                                                    <li className=""><a href="">Guitar / Base / Ukulele Group</a></li>
                                                    <li className=""><a href="">Ensemble</a></li>
                                                </ul>
                                            </li>
                                        </ul>
                                    </li>
                                </ul>

                                <a className="responsive-nav" data-toggle="#off-canvas-right" data-toggle-class="open">
                                    <span className="l-menu"/>
                                </a>
                            </nav>
                        </div>
                    </section>
                    // <nav className="navbar navbar-expand-lg navbar-light bg-light">
                    //     <button className="navbar-toggler" type="button" data-toggle="collapse"
                    //             data-target="#navbarTogglerDemo01" aria-controls="navbarTogglerDemo01"
                    //             aria-expanded="false" aria-label="Toggle navigation">
                    //         <span className="navbar-toggler-icon"/>
                    //     </button>
                    //     <div className="collapse navbar-collapse justify-content-center" id="navbarTogglerDemo01">
                    //         <div className="justify-content-start">
                    //             <Link to={(i18n.language !== "th" ? "/" + i18n.language : "") + "/"}>
                    //                 <img
                    //                     src={process.env.PUBLIC_URL + '/assets/images/navigation/mahidol-logo.png'}
                    //                     alt="mahidol tutor home logo" className="logo"/>
                    //             </Link>
                    //         </div>
